/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { useState, useContext } from "react";
import { Link, useNavigate } from 'react-router-dom';
import cookie from 'react-cookies';
import { MyUserContext } from "../../configs391/Context391";

const NavBar = () => {
  const [showInvoiceMenu, setShowInvoiceMenu] = useState(false);
  const [openMenu, setOpenMenu] = useState(false);
  const user = useContext(MyUserContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    // Xóa token và user từ cookie
    cookie.remove("token");
    cookie.remove("user");
    
    // Điều hướng về trang chủ
    navigate("/", { state: { message: "You have been logged out!" } });
  };

  return (
    <nav css={navStyle}>
      <div css={logoStyle}>Tan_Hotel</div>
      <ul css={navLinksStyle}>
        <li><Link to="/" css={linkStyle}>Trang chủ</Link></li>
        <li><Link to="/booking" css={linkStyle}>Đặt phòng</Link></li>
        {user ? (
          <>
            <li><Link to="/info" css={linkStyle}>{user.name}</Link></li>
            <li><Link to="/" onClick={handleLogout} css={linkStyle}>Đăng xuất</Link></li>
            {user.role === 2 && (
              <li css={menuContainerStyle}>
                <span css={linkStyle}>QL phiếu đặt phòng</span>
                <ul css={dropdownStyle}>
                  <li><Link to="/reservation-list/" css={dropdownLinkStyle}>QL phiếu đặt phòng</Link></li>
                  <li><Link to="/service-list/" css={dropdownLinkStyle}>QL dịch vụ</Link></li>
                  <li><Link to="/customer-list/" css={dropdownLinkStyle}>QL khách hàng</Link></li>
                </ul>
              </li>
            )}
            {user.role === 2 && (
              <li css={invoiceMenuStyle} onMouseEnter={() => setShowInvoiceMenu(true)} onMouseLeave={() => setShowInvoiceMenu(false)}>
                <span css={linkStyle}>QL hóa đơn</span>
                {showInvoiceMenu && (
                  <ul css={dropdownStyle}>
                    <li><Link to="/list-invoice" css={dropdownLinkStyle}>Danh sách hóa đơn</Link></li>
                    <li><Link to="/manage-invoice" css={dropdownLinkStyle}>Xuất hóa đơn</Link></li>
                  </ul>
                )}
              </li>
            )}
          </>
        ) : (
          <li><Link to="/login" css={linkStyle}>Đăng nhập</Link></li>
        )}
      </ul>
      <i
        onClick={() => setOpenMenu(!openMenu)}
        id="burgerMenu"
        className={openMenu ? "fas fa-times fa-lg" : "fas fa-align-right fa-lg"}
      ></i>
    </nav>
  );
};

const navStyle = css`
  max-width: calc(100% - 20rem);
  position: fixed;
  z-index: 9999;
  left: 50%;
  transform: translateX(-50%);
  padding: 1rem 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: white;
  width: 95%;
  top: 1rem;
  box-shadow: 5px 5px 20px rgba(0, 0, 0, 0.2);
  border-radius: 1rem;
`;

const logoStyle = css`
  font-size: 1.5rem;
  font-weight: 800;
  color: var(--text-dark);
`;

const navLinksStyle = css`
  list-style: none;
  display: flex;
  align-items: center;
  gap: 2rem;
`;

const linkStyle = css`
  font-weight: 600;
  color: var(--text-light);
  transition: 0.3s;
  text-decoration: none;

  &:hover {
    color: var(--primary-color);
  }
`;

const menuContainerStyle = css`
  position: relative;
  cursor: pointer;
`;

const dropdownStyle = css`
  display: none;
  position: absolute;
  top: 100%;
  left: 0;
  background-color: white;
  list-style: none;
  padding: 0;
  margin: 0;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  border-radius: 8px;
  z-index: 999;

  ${menuContainerStyle}:hover & {
    display: block;
  }
`;

const dropdownLinkStyle = css`
  display: block;
  padding: 0.5rem 1rem;
  color: var(--text-dark);
  text-decoration: none;
  transition: 0.3s;

  &:hover {
    background-color: #f0f0f0;
    color: var(--primary-color);
  }
`;

const invoiceMenuStyle = css`
  position: relative;
  cursor: pointer;
  &:hover .dropdown {
    display: flex;
  }
`;

export default NavBar;
