// src/components/ReservationList391.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { css } from '@emotion/react';

const ReservationList391 = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/reservations/');
        setReservations(response.data);
      } catch (error) {
        setError('Error fetching reservations');
      } finally {
        setLoading(false);
      }
    };

    fetchReservations();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div css={containerStyle}>
      <h1>Danh sách phiếu đặt phòng</h1>
      <table css={tableStyle}>
        <thead>
          <tr>
            <th css={thStyle}>ID</th>
            <th css={thStyle}>Ngày đặt</th>
            <th css={thStyle}>Ngày nhận phòng</th>
            <th css={thStyle}>Ngày trả phòng</th>
            <th css={thStyle}>Phòng</th>
            <th css={thStyle}>Ngày check-in</th>
            <th css={thStyle}>Trạng thái check-in</th>
          </tr>
        </thead>
        <tbody>
          {reservations.map((reservation) => (
            <tr key={reservation.id}>
              <td css={tdStyle}>{reservation.id}</td>
              <td css={tdStyle}>{new Date(reservation.book_date).toLocaleDateString()}</td>
              <td css={tdStyle}>{new Date(reservation.checkin).toLocaleDateString()}</td>
              <td css={tdStyle}>{new Date(reservation.checkout).toLocaleDateString()}</td>
              <td css={tdStyle}>{reservation.room.join(', ')}</td> {/* Hiển thị ID phòng, nối với dấu phẩy */}
              <td css={tdStyle}>{reservation.check_in_date ? new Date(reservation.check_in_date).toLocaleDateString() : 'Chưa check-in'}</td>
              <td css={tdStyle}>{reservation.status_checkin ? 'Đã check-in' : 'Chưa check-in'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

const containerStyle = css`
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
`;

const tableStyle = css`
  width: 100%;
  border-collapse: collapse;
  margin-top: 20px;
`;

const thStyle = css`
  background-color: #f4f4f4;
  padding: 10px;
  text-align: left;
  border-bottom: 1px solid #ddd;
`;

const tdStyle = css`
  padding: 10px;
  border-bottom: 1px solid #ddd;
`;

export default ReservationList391;
